package aug20;

public class Sysoutwithmathtable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int table = 10;
		for (int i = 1; i <= 10; i++) {
			System.out.println(table + " x " + i + " = " + table * i);
		}
		int a = 2;
		for (int i = 1; i <= 10; i++) {

			System.out.println(a + " x " + i + " = " + a * i);
		}

	}
}
